import React, { useState, useEffect } from 'react';
import './PlaneView.css';

const seatsLayout = [
  // Define seats with types and positions based on the image
  { id: '1A', type: 'business', row: 1, col: 1 },
  { id: '1B', type: 'business', row: 1, col: 2 },
  { id: '1C', type: 'business', row: 1, col: 3 },
  { id: '1D', type: 'business', row: 1, col: 4 },
  { id: '1E', type: 'business', row: 1, col: 5 },
  { id: '1F', type: 'business', row: 1, col: 6 },
  { id: '2A', type: 'economy', row: 2, col: 1 },
  { id: '2B', type: 'economy', row: 2, col: 2 },
  { id: '2C', type: 'economy', row: 2, col: 3 },
  { id: '2D', type: 'economy', row: 2, col: 4 },
  { id: '2E', type: 'economy', row: 2, col: 5 },
  { id: '2F', type: 'economy', row: 2, col: 6 },
  // Add more seats as needed
];

const PlaneView = () => {
  const [passengers, setPassengers] = useState([]);
  const [hoveredPassenger, setHoveredPassenger] = useState(null);
  const [hoverPosition, setHoverPosition] = useState({ top: 0, left: 0 });
  const [hoverVisible, setHoverVisible] = useState(false);

  useEffect(() => {
    // Fetch passengers
    fetch('http://localhost:8000/api/passengerinfo/flight/ABC123/')
      .then(response => response.json())
      .then(data => {
        console.log('Passengers fetched:', data);
        setPassengers(data);
      })
      .catch(error => console.error('Error fetching passengers:', error));
  }, []);

  const handleMouseEnter = (passenger, event) => {
    const rect = event.target.getBoundingClientRect();
    setHoverPosition({
      top: rect.top + window.scrollY - 100, // Adjust this value to position the hover box
      left: rect.left + window.scrollX
    });
    setHoveredPassenger(passenger);
    setHoverVisible(true);
  };

  const handleMouseLeave = () => {
    setHoverVisible(false);
  };

  return (
    <div className="plane-container">
      <div className="plane">
        {seatsLayout.map(seat => {
          const passenger = passengers.find(p => p.seat_number === seat.id);
          return (
            <div
              key={seat.id}
              className={`seat ${seat.type}`}
              onMouseEnter={(event) => passenger && handleMouseEnter(passenger, event)}
              onMouseLeave={handleMouseLeave}
              style={{
                gridRow: seat.row,
                gridColumn: seat.col,
              }}
            >
              <span>{seat.id}</span>
            </div>
          );
        })}
      </div>
      {hoverVisible && hoveredPassenger && (
        <div className="hover-info" style={{ top: hoverPosition.top, left: hoverPosition.left }}>
          <p><strong>Name:</strong> {hoveredPassenger.passenger_name}</p>
          <p><strong>Age:</strong> {hoveredPassenger.age}</p>
          <p><strong>Gender:</strong> {hoveredPassenger.gender}</p>
          <p><strong>Nationality:</strong> {hoveredPassenger.nationality}</p>
        </div>
      )}
    </div>
  );
};

export default PlaneView;
