import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFlight } from '../context/FlightContext'; // Ensure the path is correct

const SelectFlight = () => {
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState('');
  const navigate = useNavigate();
  const { setSelectedFlight: setGlobalSelectedFlight } = useFlight();

  useEffect(() => {
    fetch('http://localhost:8000/api/flightinfo/all-flights/') // Replace with your API endpoint
      .then(response => response.json())
      .then(data => setFlights(data))
      .catch(error => console.error('Error fetching flights:', error));
  }, []);

  const handleSelection = (e) => {
    setSelectedFlight(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedFlight) {
      const selectedFlightData = flights.find(flight => flight.flight_number === selectedFlight);
      setGlobalSelectedFlight(selectedFlightData);
      navigate(`/home/${selectedFlight}`);
    }
  };

  return (
    <div>
      <h2>Select Flight</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="flights">Choose a flight:</label>
        <select id="flights" value={selectedFlight} onChange={handleSelection}>
          <option value="" disabled>Select a flight</option>
          {flights.map((flight) => (
            <option key={flight.flight_number} value={flight.flight_number}>
              {flight.flight_number} - {flight.source_city} to {flight.destination_city}
            </option>
          ))}
        </select>
        <button type="submit">Confirm</button>
      </form>
    </div>
  );
};

export default SelectFlight;
