import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ClipLoader from 'react-spinners/ClipLoader';
import './PassengerTable.css'; // Import CSS file for styling

const CombinedTable = ({ data }) => {
  const [selectedPerson, setSelectedPerson] = useState(null);

  const handleRowClick = (person) => {
    setSelectedPerson(person);
  };

  return (
    <div>
      <h2>All People on the Flight</h2>
      <table className="passenger-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Full Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Nationality</th>
            <th>Additional Info</th>
          </tr>
        </thead>
        <tbody>
          {data.map((person) => (
            <tr key={person.id} onClick={() => handleRowClick(person)}>
              <td>{person.id}</td>
              <td>{person.type}</td>
              <td>{person.name}</td>
              <td>{person.age}</td>
              <td>{person.gender}</td>
              <td>{person.nationality}</td>
              <td>{person.additionalInfo}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedPerson && (
        <div className="user-card">
          <h3>User Information</h3>
          <p><strong>ID:</strong> {selectedPerson.id}</p>
          <p><strong>Name:</strong> {selectedPerson.name}</p>
          <p><strong>Age:</strong> {selectedPerson.age}</p>
          <p><strong>Gender:</strong> {selectedPerson.gender}</p>
          <p><strong>Nationality:</strong> {selectedPerson.nationality}</p>
          <p><strong>Type:</strong> {selectedPerson.type}</p>
          <p><strong>Additional Info:</strong> {selectedPerson.additionalInfo}</p>
        </div>
      )}
    </div>
  );
};

const TabularView = () => {
  const { flightNumber } = useParams();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchPassengers = async () => {
      try {
        const response = await fetch(`http://159.20.87.158:8000/api/passengerinfo/flight/${flightNumber}/`);
        const passengers = await response.json();
        const formattedPassengers = passengers.map(passenger => ({
          id: passenger.passenger_id,
          type: 'Passenger',
          name: passenger.passenger_name,
          age: passenger.age,
          gender: passenger.gender,
          nationality: passenger.nationality,
          additionalInfo: `Seat Type: ${passenger.seat_type}, Children: ${
            passenger.children && passenger.children.length > 0
              ? passenger.children.map(child => child.child_name).join(', ')
              : 'No children'
          }`
        }));
        return formattedPassengers;
      } catch (error) {
        console.error('Error fetching passengers:', error);
        return [];
      }
    };

    const fetchCrew = async () => {
      try {
        const response = await fetch(`http://159.20.87.158:8080/backend/assigned-crew/${flightNumber}/`);
        const crew = await response.json();
        const formattedCrew = crew.map(crewMember => {
          const isFlightCrew = crewMember.type === 'flight';
          const id = isFlightCrew ? crewMember.pilot_id : crewMember.attendant_id;
          const additionalInfo = isFlightCrew
            ? `Vehicle Restriction: ${crewMember.vehicle_restriction || 'N/A'}, Allowed Range: ${crewMember.allowed_range || 'N/A'}, Seniority Level: ${crewMember.seniority_level || 'N/A'}`
            : `Vehicle Restrictions: ${crewMember.vehicle_restrictions || 'N/A'}, Attendant Type: ${crewMember.attendant_type || 'N/A'}, Dishes: ${
                crewMember.dish_recipes && crewMember.dish_recipes.length > 0
                  ? crewMember.dish_recipes.map(dish => dish.recipe_name).join(', ')
                  : 'Cabin Crew Member not suitable for dishes'
              }`;

          return {
            id,
            type: isFlightCrew ? 'Flight Crew' : 'Cabin Crew',
            name: crewMember.name,
            age: crewMember.age,
            gender: crewMember.gender,
            nationality: crewMember.nationality,
            additionalInfo
          };
        });
        return formattedCrew;
      } catch (error) {
        console.error('Error fetching crew:', error);
        return [];
      }
    };

    const fetchData = async () => {
      const passengers = await fetchPassengers();
      const crew = await fetchCrew();
      setData([...passengers, ...crew]);
      setLoading(false); // Set loading to false once data is fetched
    };

    fetchData();
  }, [flightNumber]);

  return (
    <div>
      <h1>Tabular View - Interior of the Plane</h1>
      {loading ? (
        <div className="loading-container">
          <ClipLoader size={50} color={"#123abc"} loading={loading} />
          <p>Loading data, please wait...</p>
        </div>
      ) : (
        <CombinedTable data={data} />
      )}
    </div>
  );
};

export default TabularView;
