import React, { useState } from 'react';
import axios from 'axios';

const CreateFlight = () => {
  const [formData, setFormData] = useState({
    flight_number: '',
    flight_date: '',
    flight_duration: '',
    flight_distance: '',
    source_country: '',
    source_city: '',
    source_airport_name: '',
    source_airport_code: '',
    destination_country: '',
    destination_city: '',
    destination_airport_name: '',
    destination_airport_code: '',
    vehicle_type: {
      vehicle_type: '',
      number_of_seats: '',
      seating_plan: '',
      crew_limit: '',
      passenger_limit: ''
    },
    shared_flight: false
  });

  const [errors, setErrors] = useState({});

  const validate = () => {
    const errors = {};
    if (!formData.flight_number) errors.flight_number = "Flight number is required";
    if (!formData.flight_date) errors.flight_date = "Flight date is required";
    if (!formData.flight_duration) errors.flight_duration = "Flight duration is required";
    if (!formData.flight_distance) errors.flight_distance = "Flight distance is required";
    if (!formData.source_country) errors.source_country = "Source country is required";
    if (!formData.source_city) errors.source_city = "Source city is required";
    if (!formData.source_airport_name) errors.source_airport_name = "Source airport name is required";
    if (!formData.source_airport_code) errors.source_airport_code = "Source airport code is required";
    if (!formData.destination_country) errors.destination_country = "Destination country is required";
    if (!formData.destination_city) errors.destination_city = "Destination city is required";
    if (!formData.destination_airport_name) errors.destination_airport_name = "Destination airport name is required";
    if (!formData.destination_airport_code) errors.destination_airport_code = "Destination airport code is required";
    if (!formData.vehicle_type.vehicle_type) errors.vehicle_type = "Vehicle type is required";
    if (!formData.vehicle_type.number_of_seats) errors.number_of_seats = "Number of seats is required";
    if (!formData.vehicle_type.seating_plan) errors.seating_plan = "Seating plan is required";
    if (!formData.vehicle_type.crew_limit) errors.crew_limit = "Crew limit is required";
    if (!formData.vehicle_type.passenger_limit) errors.passenger_limit = "Passenger limit is required";

    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name in formData.vehicle_type) {
      setFormData({
        ...formData,
        vehicle_type: {
          ...formData.vehicle_type,
          [name]: value
        }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, shared_flight: e.target.checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;

    axios.post('http://127.0.0.1:8000/api/flightinfo/save-flight/', formData)
      .then(response => {
        console.log(response.data);
        // Handle success - perhaps redirect or show a message
      })
      .catch(error => {
        console.error(error);
        // Handle error - show an error message
      });
  };

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#e3f2fd', // Light blue background
      padding: '20px',
    },
    form: {
      maxWidth: '600px',
      width: '100%',
      padding: '40px',
      backgroundColor: 'white',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      textAlign: 'center',
      overflowY: 'auto',
      maxHeight: '80vh', // Ensure the form is scrollable if content exceeds viewport
    },
    header: {
      color: '#1976d2', // Blue header
      fontSize: '2em',
      marginBottom: '20px',
    },
    label: {
      display: 'block',
      marginBottom: '10px',
      color: '#333',
      fontWeight: 'bold',
      fontSize: '1.2em',
    },
    input: {
      width: '100%',
      padding: '15px',
      marginBottom: '20px',
      border: '1px solid #ddd',
      borderRadius: '5px',
      backgroundColor: '#f9f9f9',
      fontSize: '1em',
    },
    error: {
      color: 'red',
      marginBottom: '20px',
    },
    checkboxContainer: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '20px',
    },
    checkboxLabel: {
      marginLeft: '10px',
      fontSize: '1em',
    },
    button: {
      backgroundColor: '#1976d2', // Blue button
      color: 'white',
      border: 'none',
      padding: '15px 30px',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '1.2em',
      transition: 'background-color 0.3s',
      marginTop: '20px',
    },
    buttonHover: {
      backgroundColor: '#145ca8', // Darker blue on hover
    },
  };

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2 style={styles.header}>Create Flight</h2>
        <label style={styles.label}>Flight Number:</label>
        <input type="text" name="flight_number" value={formData.flight_number} onChange={handleChange} required style={styles.input} />
        {errors.flight_number && <div style={styles.error}>{errors.flight_number}</div>}
        <label style={styles.label}>Flight Date:</label>
        <input type="datetime-local" name="flight_date" value={formData.flight_date} onChange={handleChange} required style={styles.input} />
        {errors.flight_date && <div style={styles.error}>{errors.flight_date}</div>}
        <label style={styles.label}>Flight Duration (HH:MM):</label>
        <input type="text" name="flight_duration" value={formData.flight_duration} onChange={handleChange} required style={styles.input} />
        {errors.flight_duration && <div style={styles.error}>{errors.flight_duration}</div>}
        <label style={styles.label}>Flight Distance:</label>
        <input type="number" name="flight_distance" value={formData.flight_distance} onChange={handleChange} required style={styles.input} />
        {errors.flight_distance && <div style={styles.error}>{errors.flight_distance}</div>}
        <label style={styles.label}>Source Country:</label>
        <input type="text" name="source_country" value={formData.source_country} onChange={handleChange} required style={styles.input} />
        {errors.source_country && <div style={styles.error}>{errors.source_country}</div>}
        <label style={styles.label}>Source City:</label>
        <input type="text" name="source_city" value={formData.source_city} onChange={handleChange} required style={styles.input} />
        {errors.source_city && <div style={styles.error}>{errors.source_city}</div>}
        <label style={styles.label}>Source Airport Name:</label>
        <input type="text" name="source_airport_name" value={formData.source_airport_name} onChange={handleChange} required style={styles.input} />
        {errors.source_airport_name && <div style={styles.error}>{errors.source_airport_name}</div>}
        <label style={styles.label}>Source Airport Code:</label>
        <input type="text" name="source_airport_code" value={formData.source_airport_code} onChange={handleChange} required style={styles.input} />
        {errors.source_airport_code && <div style={styles.error}>{errors.source_airport_code}</div>}
        <label style={styles.label}>Destination Country:</label>
        <input type="text" name="destination_country" value={formData.destination_country} onChange={handleChange} required style={styles.input} />
        {errors.destination_country && <div style={styles.error}>{errors.destination_country}</div>}
        <label style={styles.label}>Destination City:</label>
        <input type="text" name="destination_city" value={formData.destination_city} onChange={handleChange} required style={styles.input} />
        {errors.destination_city && <div style={styles.error}>{errors.destination_city}</div>}
        <label style={styles.label}>Destination Airport Name:</label>
        <input type="text" name="destination_airport_name" value={formData.destination_airport_name} onChange={handleChange} required style={styles.input} />
        {errors.destination_airport_name && <div style={styles.error}>{errors.destination_airport_name}</div>}
        <label style={styles.label}>Destination Airport Code:</label>
        <input type="text" name="destination_airport_code" value={formData.destination_airport_code} onChange={handleChange} required style={styles.input} />
        {errors.destination_airport_code && <div style={styles.error}>{errors.destination_airport_code}</div>}

        <h3 style={{ color: '#1976d2' }}>Vehicle Type</h3>
        <label style={styles.label}>Vehicle Type:</label>
        <input type="text" name="vehicle_type" value={formData.vehicle_type.vehicle_type} onChange={handleChange} required style={styles.input} />
        {errors.vehicle_type && <div style={styles.error}>{errors.vehicle_type}</div>}
        <label style={styles.label}>Number of Seats:</label>
        <input type="number" name="number_of_seats" value={formData.vehicle_type.number_of_seats} onChange={handleChange} required style={styles.input} />
        {errors.number_of_seats && <div style={styles.error}>{errors.number_of_seats}</div>}
        <label style={styles.label}>Seating Plan:</label>
        <input type="text" name="seating_plan" value={formData.vehicle_type.seating_plan} onChange={handleChange} required style={styles.input} />
        {errors.seating_plan && <div style={styles.error}>{errors.seating_plan}</div>}
        <label style={styles.label}>Crew Limit:</label>
        <input type="number" name="crew_limit" value={formData.vehicle_type.crew_limit} onChange={handleChange} required style={styles.input} />
        {errors.crew_limit && <div style={styles.error}>{errors.crew_limit}</div>}
        <label style={styles.label}>Passenger Limit:</label>
        <input type="number" name="passenger_limit" value={formData.vehicle_type.passenger_limit} onChange={handleChange} required style={styles.input} />
        {errors.passenger_limit && <div style={styles.error}>{errors.passenger_limit}</div>}

        <div style={styles.checkboxContainer}>
          <input type="checkbox" name="shared_flight" checked={formData.shared_flight} onChange={handleCheckboxChange} />
          <label style={styles.checkboxLabel}>Shared Flight</label>
        </div>

        <button
          type="submit"
          style={styles.button}
          onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
        >
          Create Flight
        </button>
      </form>
    </div>
  );
};

export default CreateFlight;
