import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFlight } from '../context/FlightContext';

const SelectFlight = () => {
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState('');
  const navigate = useNavigate();
  const { setSelectedFlight: setGlobalSelectedFlight } = useFlight();

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/flightinfo/all-flights/');
        const data = await response.json();
        setFlights(data);
      } catch (error) {
        console.error('Error fetching flights:', error);
      }
    };

    fetchFlights();
  }, []);

  const handleSelection = (e) => {
    setSelectedFlight(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedFlight) return;

    try {
      const selectedFlightData = flights.find(flight => flight.flight_number === selectedFlight);
      const response = await fetch(`http://localhost:8080/backend/fetch-and-assign-crew/${selectedFlight}`, {
        method: 'GET',
      });
      const data = await response.json();
      setGlobalSelectedFlight(selectedFlightData);
      navigate(`/home/${selectedFlight}`);
    } catch (error) {
      console.error('Error assigning crew:', error);
    }
  };

  const handleCreateFlight = () => {
    navigate('/create-flight');
  };

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#e3f2fd', // Light blue background
    },
    content: {
      maxWidth: '600px',
      width: '100%',
      padding: '40px',
      backgroundColor: 'white',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      textAlign: 'center',
    },
    header: {
      color: '#1976d2', // Blue header
      fontSize: '2em',
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    label: {
      marginBottom: '10px',
      color: '#333',
      fontWeight: 'bold',
      fontSize: '1.2em',
    },
    select: {
      width: '100%',
      padding: '15px',
      marginBottom: '20px',
      border: '1px solid #ddd',
      borderRadius: '5px',
      backgroundColor: '#f9f9f9',
      fontSize: '1em',
    },
    button: {
      backgroundColor: '#1976d2', // Blue button
      color: 'white',
      border: 'none',
      padding: '15px 30px',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '1.2em',
      transition: 'background-color 0.3s',
      marginBottom: '10px',
    },
    buttonHover: {
      backgroundColor: '#145ca8', // Darker blue on hover
    },
    createButton: {
      backgroundColor: '#1976d2', // Blue button for create
      color: 'white',
    },
    createButtonHover: {
      backgroundColor: '#145ca8', // Darker blue on hover
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <h2 style={styles.header}>Select Flight</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <label htmlFor="flights" style={styles.label}>Choose a flight:</label>
          <select id="flights" value={selectedFlight} onChange={handleSelection} required style={styles.select}>
            <option value="" disabled>Select a flight</option>
            {flights.map((flight) => (
              <option key={flight.flight_number} value={flight.flight_number}>
                {flight.flight_number} - {flight.source_city} to {flight.destination_city}
              </option>
            ))}
          </select>
          <button
            type="submit"
            style={styles.button}
            onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
            onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
          >
            Confirm
          </button>
        </form>
        <button
          type="button"
          onClick={handleCreateFlight}
          style={{ ...styles.button, ...styles.createButton }}
          onMouseOver={(e) => (e.target.style.backgroundColor = styles.createButtonHover.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = styles.createButton.backgroundColor)}
        >
          Create New Flight
        </button>
      </div>
    </div>
  );
};

export default SelectFlight;
