import React from 'react';
import { Button, Box, Typography, Container } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import FlightIcon from '@mui/icons-material/Flight';
import PublicIcon from '@mui/icons-material/Public';
import TableViewIcon from '@mui/icons-material/TableView';

const Homepage = () => {
  const navigate = useNavigate();
  const { flightNumber } = useParams();

  return (
    <Container component="main" maxWidth="sm">
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          background: 'linear-gradient(180deg, #e0f7fa 0%, #80deea 100%)',
          padding: 4,
          borderRadius: 2,
          boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.12)',
        }}
      >
        <Typography component="h1" variant="h4" gutterBottom>
          Welcome to AirManage
        </Typography>
        <Typography variant="h6" color="textSecondary" gutterBottom>
          Your Flight Management Dashboard
        </Typography>
        <Box sx={{ mt: 4, width: '100%' }}>
          <Button
            startIcon={<TableViewIcon />}
            variant="contained"
            sx={{ width: '100%', mb: 2, bgcolor: 'primary.light' }}
            onClick={() => navigate(`/tabular-view/${flightNumber}`)}
          >
            Tabular View
          </Button>
          <Button
            startIcon={<FlightIcon />}
            variant="contained"
            sx={{ width: '100%', mb: 2, bgcolor: 'secondary.light' }}
            onClick={() => navigate(`/plane-view/${flightNumber}`)}
          >
            Plane View
          </Button>
          <Button
            startIcon={<PublicIcon />}
            variant="contained"
            sx={{ width: '100%', mb: 2, bgcolor: 'info.light' }}
            onClick={() => navigate(`/extended-view/${flightNumber}`)}
          >
            Extended View
          </Button>
        </Box>
      </Box>
    </Container>
  );
};

export default Homepage;
