        <label style={styles.label}>Seating Plan:</label>
        <input type="text" name="seating_plan" value={formData.vehicle_type.seating_plan} onChange={handleChange} required style={styles.input} />
        <label style={styles.label}>Crew Limit:</label>
        <input type="number" name="crew_limit" value={formData.vehicle_type.crew_limit} onChange={handleChange} required style={styles.input} />
        <label style={styles.label}>Passenger Limit:</label>
        <input type="number" name="passenger_limit" value={formData.vehicle_type.passenger_limit} onChange={handleChange} required style={styles.input} />

        <div style={styles.checkboxContainer}>
          <input type="checkbox" name="shared_flight" checked={formData.shared_flight} onChange={handleCheckboxChange} />
          <label style={styles.checkboxLabel}>Shared Flight</label>
        </div>

        <button
          type="submit"
          style={styles.button}
          onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
        >
          Create Flight
        </button>
      </form>
    </div>
  );
};

export default CreateFlight;
