import React, { useState, useEffect, useRef } from 'react';
import './PlaneView.css';
import planeSvg from './United_Airlines_Boeing_737-700_B.svg'; // Adjust the path as needed

const PlaneView = () => {
  const [passengers, setPassengers] = useState([]);
  const [hoveredPassenger, setHoveredPassenger] = useState(null);
  const [hoverPosition, setHoverPosition] = useState({ top: 0, left: 0 });
  const [hoverVisible, setHoverVisible] = useState(false);
  const svgRef = useRef(null);

  useEffect(() => {
    // Fetch passengers
    fetch('http://localhost:8000/api/passengerinfo/flight/ABC123/')
      .then(response => response.json())
      .then(data => {
        console.log('Passengers fetched:', data);
        setPassengers(data);
      })
      .catch(error => console.error('Error fetching passengers:', error));
  }, []);

  useEffect(() => {
    if (svgRef.current) {
      const svg = svgRef.current.contentDocument;
      const seats = svg.querySelectorAll('.seat'); // Ensure seat elements have the class 'seat'

      seats.forEach(seat => {
        const seatId = seat.getAttribute('id');
        const passenger = passengers.find(p => p.seat_number === seatId);

        seat.addEventListener('mouseenter', event => {
          if (passenger) {
            const rect = event.target.getBoundingClientRect();
            setHoverPosition({
              top: rect.top + window.scrollY - 100, // Adjust this value to position the hover box
              left: rect.left + window.scrollX
            });
            setHoveredPassenger(passenger);
            setHoverVisible(true);
          }
        });

        seat.addEventListener('mouseleave', () => {
          setHoverVisible(false);
        });
      });
    }
  }, [passengers]);

  return (
    <div className="plane-container">
      <object ref={svgRef} type="image/svg+xml" data={planeSvg} className="plane"></object>
      {hoverVisible && hoveredPassenger && (
        <div className="hover-info" style={{ top: hoverPosition.top, left: hoverPosition.left }}>
          <p><strong>Name:</strong> {hoveredPassenger.passenger_name}</p>
          <p><strong>Age:</strong> {hoveredPassenger.age}</p>
          <p><strong>Gender:</strong> {hoveredPassenger.gender}</p>
          <p><strong>Nationality:</strong> {hoveredPassenger.nationality}</p>
        </div>
      )}
    </div>
  );
};

export default PlaneView;
