import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ClipLoader from 'react-spinners/ClipLoader';
import './ExtendedView.css';

const PassengersTable = ({ passengers, onPassengerSelect, onDeletePassenger, onEditPassenger }) => (
  <div>
    <h2>Passengers</h2>
    <table className="passengers-table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Full Name</th>
          <th>Age</th>
          <th>Gender</th>
          <th>Nationality</th>
          <th>Seat Type</th>
          <th>Children</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {passengers.map((passenger) => (
          <tr key={passenger.passenger_id}>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.passenger_id}</td>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.passenger_name}</td>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.age}</td>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.gender}</td>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.nationality}</td>
            <td onClick={() => onPassengerSelect(passenger)}>{passenger.seat_type}</td>
            <td onClick={() => onPassengerSelect(passenger)}>
              {passenger.children && passenger.children.length > 0 ? (
                <ul>
                  {passenger.children.map(child => (
                    <li key={child.child_id}>{child.child_name}</li>
                  ))}
                </ul>
              ) : (
                "No children"
              )}
            </td>
            <td>
              <div className="button-group">
                <button onClick={() => onEditPassenger(passenger)}>Edit</button>
                <button onClick={() => onDeletePassenger(passenger.passenger_id)}>Delete</button>
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const CrewTable = ({ crew, onCrewMemberSelect, crewType }) => (
  <div>
    <h2>{crewType} Crew</h2>
    <table className="crew-table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Age</th>
          <th>Nationality</th>
          <th>Languages</th>
          <th>Seniority Level</th>
          {crew[0]?.vehicle_restriction && <th>Vehicle Restriction</th>}
          {crew[0]?.allowed_range && <th>Allowed Range</th>}
          {crewType === 'Cabin' && <th>Dishes</th>}
        </tr>
      </thead>
      <tbody>
        {crew.map((member) => (
          <tr key={member.pilot_id || member.attendant_id} onClick={() => onCrewMemberSelect(member)}>
            <td>{member.pilot_id || member.attendant_id}</td>
            <td>{member.name}</td>
            <td>{member.age}</td>
            <td>{member.nationality}</td>
            <td>{member.known_languages}</td>
            <td>{member.seniority_level || member.attendant_type}</td>
            {member.vehicle_restriction && <td>{member.vehicle_restriction}</td>}
            {member.allowed_range && <td>{member.allowed_range}</td>}
            {crewType === 'Cabin' && (
              <td>
                {member.dish_recipes && member.dish_recipes.length > 0 ? (
                  <ul>
                    {member.dish_recipes.map((dish) => (
                      <li key={dish.id}>{dish.recipe_name}</li>
                    ))}
                  </ul>
                ) : (
                  "Cabin Crew Member not suitable for dishes"
                )}
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const UserCard = ({ user }) => (
  <div className="user-card">
    <h3>User Information</h3>
    <p><strong>ID:</strong> {user.pilot_id || user.attendant_id || user.passenger_id}</p>
    <p><strong>Name:</strong> {user.name || user.passenger_name}</p>
    <p><strong>Age:</strong> {user.age}</p>
    <p><strong>Gender:</strong> {user.gender}</p>
    <p><strong>Nationality:</strong> {user.nationality}</p>
    {user.seat_type && <p><strong>Seat Type:</strong> {user.seat_type}</p>}
    {user.vehicle_restriction && <p><strong>Vehicle Restriction:</strong> {user.vehicle_restriction}</p>}
    {user.allowed_range && <p><strong>Allowed Range:</strong> {user.allowed_range}</p>}
    {user.known_languages && <p><strong>Languages:</strong> {user.known_languages}</p>}
    {user.attendant_type && <p><strong>Crew Type:</strong> {user.attendant_type}</p>}
    {user.seniority_level && <p><strong>Seniority Level:</strong> {user.seniority_level}</p>}
    {user.children && user.children.length > 0 && (
      <div>
        <h4>Children</h4>
        <ul>
          {user.children.map(child => (
            <li key={child.child_id}>{child.child_name}</li>
          ))}
        </ul>
      </div>
    )}
    {user.dish_recipes && user.dish_recipes.length > 0 ? (
      <div>
        <h4>Dishes</h4>
        <ul>
          {user.dish_recipes.map((dish) => (
            <li key={dish.id}>{dish.recipe_name}</li>
          ))}
        </ul>
      </div>
    ) : (
      <p>Cabin Crew Member not suitable for dishes</p>
    )}
  </div>
);

const ExtendedView = () => {
  const { flightNumber } = useParams();
  const [passengers, setPassengers] = useState([]);
  const [flightCrew, setFlightCrew] = useState([]);
  const [cabinCrew, setCabinCrew] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    // Fetch passengers
    fetch(`http://159.20.87.158:8000/api/passengerinfo/flight/${flightNumber}/`)
      .then(response => response.json())
      .then(data => {
        setPassengers(data);
        setLoading(false); // Set loading to false after fetching passengers
      })
      .catch(error => {
        console.error('Error fetching passengers:', error);
        setLoading(false); // Set loading to false even if there's an error
      });

    // Fetch assigned crew
    fetch(`http://159.20.87.158:8080/backend/assigned-crew/${flightNumber}/`, {
      method: 'GET',
    })
      .then(response => response.json())
      .then(data => {
        const flightCrew = data.filter(member => member.type === 'flight');
        const cabinCrew = data.filter(member => member.type === 'cabin');
        setFlightCrew(flightCrew);
        setCabinCrew(cabinCrew);
      })
      .catch(error => console.error('Error fetching crew:', error));
  }, [flightNumber]);

  const handlePassengerSelect = (passenger) => {
    setSelectedUser(passenger);
  };

  const handleCrewMemberSelect = (member) => {
    setSelectedUser(member);
  };

  const handleDeletePassenger = async (passengerId) => {
    try {
      const response = await fetch(`http://159.20.87.158:8000/api/passengerinfo/delete-passenger/${passengerId}/`, {
        method: 'DELETE',
      });
      if (response.status === 204) {
        setPassengers(passengers.filter(passenger => passenger.passenger_id !== passengerId));
      } else {
        console.error('Error deleting passenger:', response.statusText);
      }
    } catch (error) {
      console.error('Error deleting passenger:', error);
    }
  };

  const handleEditPassenger = (passenger) => {
    // This function can handle the edit logic, such as opening a modal with a form to edit passenger details
    console.log('Edit passenger:', passenger);
    // Implement your edit logic here
  };

  return (
    <div>
      <h1>Extended View</h1>
      {loading ? (
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '80vh',
            flexDirection: 'column'
          }}
        >
          <ClipLoader size={50} color={"#123abc"} loading={loading} />
          <p>Loading data, please wait...</p>
        </div>
      ) : (
        <>
          <PassengersTable passengers={passengers} onPassengerSelect={handlePassengerSelect} onDeletePassenger={handleDeletePassenger} onEditPassenger={handleEditPassenger} />
          <CrewTable crew={flightCrew} onCrewMemberSelect={handleCrewMemberSelect} crewType="Flight" />
          <CrewTable crew={cabinCrew} onCrewMemberSelect={handleCrewMemberSelect} crewType="Cabin" />
          {selectedUser && <UserCard user={selectedUser} />}
        </>
      )}
    </div>
  );
};

export default ExtendedView;
