import React, { useState, useEffect } from 'react';
import './PlaneView.css';

const seatsLayout = [
  // First class
  { id: '1A', type: 'first', row: 2, col: 1 },
  { id: '1B', type: 'first', row: 2, col: 2 },
  { id: '1E', type: 'first', row: 2, col: 5 },
  { id: '1F', type: 'first', row: 2, col: 6 },

  { id: '2A', type: 'first', row: 3, col: 1 },
  { id: '2B', type: 'first', row: 3, col: 2 },
  { id: '2E', type: 'first', row: 3, col: 5 },
