  // Business class
  { id: '3A', type: 'business', row: 5, col: 1 },
  { id: '3B', type: 'business', row: 5, col: 2 },
  { id: '3E', type: 'business', row: 5, col: 5 },
  { id: '3F', type: 'business', row: 5, col: 6 },

  { id: '4A', type: 'business', row: 6, col: 1 },
  { id: '4B', type: 'business', row: 6, col: 2 },
  { id: '4E', type: 'business', row: 6, col: 5 },
  { id: '4F', type: 'business', row: 6, col: 6 },

  { id: '5A', type: 'business', row: 7, col: 1 },
  { id: '5B', type: 'business', row: 7, col: 2 },
  { id: '5E', type: 'business', row: 7, col: 5 },
  { id: '5F', type: 'business', row: 7, col: 6 },

  { id: '6A', type: 'business', row: 8, col: 1 },
  { id: '6B', type: 'business', row: 8, col: 2 },
  { id: '6E', type: 'business', row: 8, col: 5 },
  { id: '6F', type: 'business', row: 8, col: 6 },

  // Economy class
  { id: '7A', type: 'economy', row: 10, col: 1 },
  { id: '7B', type: 'economy', row: 10, col: 2 },
  { id: '7E', type: 'economy', row: 10, col: 5 },
  { id: '7F', type: 'economy', row: 10, col: 6 },

  { id: '8A', type: 'economy', row: 11, col: 1 },
  { id: '8B', type: 'economy', row: 11, col: 2 },
  { id: '8E', type: 'economy', row: 11, col: 5 },
  { id: '8F', type: 'economy', row: 11, col: 6 },

  { id: '9A', type: 'economy', row: 12, col: 1 },
  { id: '9B', type: 'economy', row: 12, col: 2 },
  { id: '9E', type: 'economy', row: 12, col: 5 },
  { id: '9F', type: 'economy', row: 12, col: 6 },

  { id: '10A', type: 'economy', row: 13, col: 1 },
  { id: '10B', type: 'economy', row: 13, col: 2 },
  { id: '10E', type: 'economy', row: 13, col: 5 },
  { id: '10F', type: 'economy', row: 13, col: 6 },

  { id: '11A', type: 'economy', row: 14, col: 1 },
  { id: '11B', type: 'economy', row: 14, col: 2 },
  { id: '11E', type: 'economy', row: 14, col: 5 },
  { id: '11F', type: 'economy', row: 14, col: 6 },

  { id: '12A', type: 'economy', row: 15, col: 1 },
  { id: '12B', type: 'economy', row: 15, col: 2 },
  { id: '12E', type: 'economy', row: 15, col: 5 },
