  { id: '13B', type: 'economy', row: 16, col: 2 },
  { id: '13E', type: 'economy', row: 16, col: 5 },
  { id: '13F', type: 'economy', row: 16, col: 6 },

  { id: '14A', type: 'economy', row: 17, col: 1 },
  { id: '14B', type: 'economy', row: 17, col: 2 },
  { id: '14E', type: 'economy', row: 17, col: 5 },
  { id: '14F', type: 'economy', row: 17, col: 6 },

  { id: '15A', type: 'economy', row: 18, col: 1 },
  { id: '15B', type: 'economy', row: 18, col: 2 },
  { id: '15E', type: 'economy', row: 18, col: 5 },
  { id: '15F', type: 'economy', row: 18, col: 6 },

  { id: '16A', type: 'economy', row: 19, col: 1 },
  { id: '16B', type: 'economy', row: 19, col: 2 },
  { id: '16E', type: 'economy', row: 19, col: 5 },
  { id: '16F', type: 'economy', row: 19, col: 6 },

  { id: '17A', type: 'economy', row: 20, col: 1 },
  { id: '17B', type: 'economy', row: 20, col: 2 },
  { id: '17E', type: 'economy', row: 20, col: 5 },
  { id: '17F', type: 'economy', row: 20, col: 6 },
];




const PlaneView = ({ flightId }) => {
  const [passengers, setPassengers] = useState([]);
  const [hoveredPassenger, setHoveredPassenger] = useState(null);
  const [hoverPosition, setHoverPosition] = useState({ top: 0, left: 0 });
  const [hoverVisible, setHoverVisible] = useState(false);

  useEffect(() => {
    // Mock fetching passengers data
    setPassengers([
      { seat_number: '1A', passenger_name: 'John Doe', age: 30, gender: 'Male', nationality: 'USA' },
      { seat_number: '7B', passenger_name: 'Jane Doe', age: 25, gender: 'Female', nationality: 'UK' }
      // Add more mock data if needed
    ]);
  }, []);

  const handleMouseEnter = (passenger, event) => {
    const rect = event.target.getBoundingClientRect();
    setHoverPosition({
      top: rect.top + window.scrollY - 100,
      left: rect.left + window.scrollX
    });
    setHoveredPassenger(passenger);
    setHoverVisible(true);
  };

  const handleMouseLeave = () => {
    setHoverVisible(false);
  };

  const handleSeatClick = (passenger) => {
    alert(`Passenger Details:\n\nName: ${passenger.passenger_name}\nAge: ${passenger.age}\nGender: ${passenger.gender}\nNationality: ${passenger.nationality}`);
  };

  return (
    <div className="plane-container">
      <div className="plane">
        <div className="banner first-class-banner">FIRST</div>
        <div className="banner business-class-banner">BUSINESS</div>
        <div className="banner economy-class-banner">ECONOMY</div>
        {seatsLayout.map(seat => {
          const passenger = passengers.find(p => p.seat_number === seat.id);
          return (
            <div
              key={seat.id}
              className={`seat ${seat.type}`}
              onMouseEnter={(event) => passenger && handleMouseEnter(passenger, event)}
              onMouseLeave={handleMouseLeave}
              onClick={() => passenger && handleSeatClick(passenger)}
              style={{
                gridRow: seat.row,
                gridColumn: seat.col,
              }}
            >
              <span>{seat.id}</span>
            </div>
          );
        })}
        <div className="aisle" style={{ gridColumn: '3 / span 2', gridRow: '1 / -1' }}></div>
        <div className="exit-row" style={{ gridColumn: '1 / -1', gridRow: '7' }}></div>
        <div className="exit-row" style={{ gridColumn: '1 / -1', gridRow: '12' }}></div>
      </div>
      {hoverVisible && hoveredPassenger && (
        <div className="hover-info" style={{ top: hoverPosition.top, left: hoverPosition.left }}>
          <p><strong>Name:</strong> {hoveredPassenger.passenger_name}</p>
          <p><strong>Age:</strong> {hoveredPassenger.age}</p>
          <p><strong>Gender:</strong> {hoveredPassenger.gender}</p>
          <p><strong>Nationality:</strong> {hoveredPassenger.nationality}</p>
        </div>
      )}
    </div>
  );
};

export default PlaneView;
