import React, { useState } from 'react';
import axios from 'axios';
import { Button, TextField, Box, Typography, Container, CircularProgress } from '@mui/material';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleLogin = async (event) => {
        event.preventDefault();
        setLoading(true);
        setError('');
        try {
            const response = await axios.post('http://localhost:8080/backend/login/', {
                username: username,
                password: password
            });
            const token = response.data.token;
            localStorage.setItem('token', token);
            window.location.href = '/select-flight'; // Change '/dashboard' to your desired route
        } catch (error) {
            setLoading(false);
            if (error.response && error.response.status === 401) {
                setError('Invalid username or password');
            } else {
                setError('An error occurred. Please try again later.');
            }
        }
    };

    const styles = {
        container: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundColor: '#e3f2fd', // Light blue background
        },
        content: {
            maxWidth: '500px',
            width: '100%',
            padding: '60px',
            backgroundColor: 'white',
            borderRadius: '15px',
            boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
            textAlign: 'center',
        },
        header: {
            color: '#1976d2', // Blue header
            fontSize: '2em',
            marginBottom: '20px',
        },
        form: {
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
        },
        input: {
            margin: '10px 0',
        },
        button: {
            backgroundColor: '#1976d2', // Blue button
            color: 'white',
            border: 'none',
            padding: '15px',
            borderRadius: '30px', // Rounder buttons
            cursor: 'pointer',
            fontSize: '1.2em',
            transition: 'background-color 0.3s, transform 0.2s',
            marginTop: '20px',
        },
        buttonHover: {
            backgroundColor: '#145ca8', // Darker blue on hover
        },
        errorText: {
            fontSize: '1rem',
            color: 'red',
            marginTop: '10px',
        },
    };

    return (
        <div style={styles.container}>
            <div style={styles.content}>
                <Typography component="h1" variant="h4" sx={{ fontSize: '2rem', fontWeight: 'bold' }}>
                    Log in to your account
                </Typography>
                <Box component="form" onSubmit={handleLogin} noValidate sx={{ mt: 3, width: '100%' }}>
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="username"
                        label="Username"
                        name="username"
                        autoComplete="username"
                        autoFocus
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        aria-label="Username"
                        InputLabelProps={{ style: { fontSize: '1.2rem' } }}
                        inputProps={{ style: { fontSize: '1.2rem', padding: '14px' } }}
                        sx={styles.input}
                    />
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        name="password"
                        label="Password"
                        type="password"
                        id="password"
                        autoComplete="current-password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        aria-label="Password"
                        InputLabelProps={{ style: { fontSize: '1.2rem' } }}
                        inputProps={{ style: { fontSize: '1.2rem', padding: '14px' } }}
                        sx={styles.input}
                    />
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        sx={{
                            mt: 4,
                            mb: 2,
                            padding: '16px 0',
                            borderRadius: '30px',
                            fontSize: '1.2rem',
                            '&:hover': {
                                backgroundColor: '#0044cc', // Change this to the color you want on hover
                            },
                        }}
                        disabled={loading}
                    >
                        {loading ? <CircularProgress size={24} /> : 'Log in'}
                    </Button>
                    {error && (
                        <Typography variant="body2" color="error" align="center" sx={styles.errorText}>
                            {error}
                        </Typography>
                    )}
                </Box>
            </div>
        </div>
    );
};

export default Login;
