import React, { useState } from 'react';
import axios from 'axios';

const CreateFlight = () => {
  const [formData, setFormData] = useState({
    flight_number: '',
    flight_date: '',
    flight_duration: '',
    flight_distance: '',
    source_country: '',
    source_city: '',
    source_airport_name: '',
    source_airport_code: '',
    destination_country: '',
    destination_city: '',
    destination_airport_name: '',
    destination_airport_code: '',
    vehicle_type: {
      vehicle_type: '',
      number_of_seats: '',
      seating_plan: '',
      crew_limit: '',
      passenger_limit: ''
    },
    shared_flight: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name in formData.vehicle_type) {
      setFormData({
        ...formData,
        vehicle_type: {
          ...formData.vehicle_type,
          [name]: value
        }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, shared_flight: e.target.checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:8000/api/flightinfo/save-flight/', formData)
