import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './PassengerTable.css'; // Import CSS file for styling

const CombinedTable = ({ data }) => {
  const [selectedPerson, setSelectedPerson] = useState(null);

  const handleRowClick = (person) => {
    setSelectedPerson(person);
  };

  return (
    <div>
      <h2>All People on the Flight</h2>
      <table className="passenger-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Type</th>
            <th>Full Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Nationality</th>
            <th>Additional Info</th>
          </tr>
        </thead>
        <tbody>
          {data.map((person) => (
            <tr key={person.id} onClick={() => handleRowClick(person)}>
              <td>{person.id}</td>
              <td>{person.type}</td>
              <td>{person.name}</td>
              <td>{person.age}</td>
              <td>{person.gender}</td>
              <td>{person.nationality}</td>
              <td>{person.additionalInfo}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedPerson && (
        <div className="user-card">
          <h3>User Information</h3>
          <p><strong>ID:</strong> {selectedPerson.id}</p>
          <p><strong>Name:</strong> {selectedPerson.name}</p>
          <p><strong>Age:</strong> {selectedPerson.age}</p>
          <p><strong>Gender:</strong> {selectedPerson.gender}</p>
          <p><strong>Nationality:</strong> {selectedPerson.nationality}</p>
          <p><strong>Type:</strong> {selectedPerson.type}</p>
          <p><strong>Additional Info:</strong> {selectedPerson.additionalInfo}</p>
        </div>
      )}
    </div>
  );
};

const TabularView = () => {
  const { flightNumber } = useParams();
  const [data, setData] = useState([]);

  useEffect(() => {
    // Fetch passengers
    fetch(`http://localhost:8000/api/passengerinfo/flight/${flightNumber}/`)
      .then(response => response.json())
      .then(passengers => {
        const formattedPassengers = passengers.map(passenger => ({
          id: passenger.passenger_id,
          type: 'Passenger',
          name: passenger.passenger_name,
          age: passenger.age,
          gender: passenger.gender,
          nationality: passenger.nationality,
          additionalInfo: `Seat Type: ${passenger.seat_type}, Children: ${
            passenger.children && passenger.children.length > 0
              ? passenger.children.map(child => child.child_name).join(', ')
              : 'No children'
          }`
        }));
        
        // Fetch assigned crew
        fetch(`http://localhost:8000/backend/assigned-crew/${flightNumber}/`)
          .then(response => response.json())
          .then(assignedCrew => {
            const formattedCrew = assignedCrew.map(crew => {
              if (crew.type === 'Flight Crew') {
                return {
                  id: crew.pilot_id,
                  type: 'Flight Crew',
                  name: crew.name,
                  age: crew.age,
                  gender: crew.gender,
                  nationality: crew.nationality,
                  additionalInfo: `Vehicle Restriction: ${crew.vehicle_restriction}, Allowed Range: ${crew.allowed_range}, Seniority Level: ${crew.seniority_level}`
                };
              } else if (crew.type === 'Cabin Crew') {
                return {
                  id: crew.attendant_id,
                  type: 'Cabin Crew',
                  name: crew.name,
                  age: crew.age,
                  gender: crew.gender,
                  nationality: crew.nationality,
                  additionalInfo: `Vehicle Restrictions: ${crew.vehicle_restrictions}, Attendant Type: ${crew.attendant_type}, Dishes: ${
                    crew.dish_recipes && crew.dish_recipes.length > 0
                      ? crew.dish_recipes.map(dish => dish.recipe_name).join(', ')
                      : 'Cabin Crew Member not suitable for dishes'
                  }`
                };
              }
              return null; // This should never happen
            }).filter(crew => crew !== null);

            // Combine all data
            setData([...formattedPassengers, ...formattedCrew]);
          })
          .catch(error => console.error('Error fetching crew:', error));
      })
      .catch(error => console.error('Error fetching passengers:', error));
  }, [flightNumber]);

  return (
    <div>
      <h1>Tabular View - Interior of the Plane</h1>
      <CombinedTable data={data} />
    </div>
  );
};

export default TabularView;
