import React, { useState } from 'react';
import axios from 'axios';

const CreateFlight = () => {
  const [formData, setFormData] = useState({
    flight_number: '',
    flight_date: '',
    flight_duration: '',
    flight_distance: '',
    source_country: '',
    source_city: '',
    source_airport_name: '',
    source_airport_code: '',
    destination_country: '',
    destination_city: '',
    destination_airport_name: '',
    destination_airport_code: '',
    vehicle_type: {
      vehicle_type: '',
      number_of_seats: '',
      seating_plan: '',
      crew_limit: '',
      passenger_limit: ''
    },
    shared_flight: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name in formData.vehicle_type) {
      setFormData({
        ...formData,
        vehicle_type: {
          ...formData.vehicle_type,
          [name]: value
        }
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleCheckboxChange = (e) => {
    setFormData({ ...formData, shared_flight: e.target.checked });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:8000/api/flightinfo/save-flight/', formData)
      .then(response => {
        console.log(response.data);
        // Handle success - perhaps redirect or show a message
      })
      .catch(error => {
        console.error(error);
        // Handle error - show an error message
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Flight</h2>
      <label>Flight Number:</label>
      <input type="text" name="flight_number" value={formData.flight_number} onChange={handleChange} required />
      <label>Flight Date:</label>
      <input type="datetime-local" name="flight_date" value={formData.flight_date} onChange={handleChange} required />
      <label>Flight Duration (HH:MM):</label>
      <input type="text" name="flight_duration" value={formData.flight_duration} onChange={handleChange} required />
      <label>Flight Distance:</label>
      <input type="number" name="flight_distance" value={formData.flight_distance} onChange={handleChange} required />
      <label>Source Country:</label>
      <input type="text" name="source_country" value={formData.source_country} onChange={handleChange} required />
      <label>Source City:</label>
      <input type="text" name="source_city" value={formData.source_city} onChange={handleChange} required />
      <label>Source Airport Name:</label>
      <input type="text" name="source_airport_name" value={formData.source_airport_name} onChange={handleChange} required />
      <label>Source Airport Code:</label>
      <input type="text" name="source_airport_code" value={formData.source_airport_code} onChange={handleChange} required />
      <label>Destination Country:</label>
      <input type="text" name="destination_country" value={formData.destination_country} onChange={handleChange} required />
      <label>Destination City:</label>
      <input type="text" name="destination_city" value={formData.destination_city} onChange={handleChange} required />
      <label>Destination Airport Name:</label>
      <input type="text" name="destination_airport_name" value={formData.destination_airport_name} onChange={handleChange} required />
      <label>Destination Airport Code:</label>
      <input type="text" name="destination_airport_code" value={formData.destination_airport_code} onChange={handleChange} required />

      <h3>Vehicle Type</h3>
      <label>Vehicle Type:</label>
      <input type="text" name="vehicle_type" value={formData.vehicle_type.vehicle_type} onChange={handleChange} required />
      <label>Number of Seats:</label>
      <input type="number" name="number_of_seats" value={formData.vehicle_type.number_of_seats} onChange={handleChange} required />
      <label>Seating Plan:</label>
      <input type="text" name="seating_plan" value={formData.vehicle_type.seating_plan} onChange={handleChange} required />
      <label>Crew Limit:</label>
      <input type="number" name="crew_limit" value={formData.vehicle_type.crew_limit} onChange={handleChange} required />
      <label>Passenger Limit:</label>
      <input type="number" name="passenger_limit" value={formData.vehicle_type.passenger_limit} onChange={handleChange} required />

      <label>Shared Flight:</label>
      <input type="checkbox" name="shared_flight" checked={formData.shared_flight} onChange={handleCheckboxChange} />

      <button type="submit">Create Flight</button>
    </form>
  );
};

export default CreateFlight;
