import React from 'react';
import { Button, Box, Typography, Container } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import FlightIcon from '@mui/icons-material/Flight';
import PublicIcon from '@mui/icons-material/Public';
import TableViewIcon from '@mui/icons-material/TableView';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#007BFF',
    },
    secondary: {
      main: '#6C757D',
    },
    info: {
      main: '#17A2B8',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

const Homepage = () => {
  const navigate = useNavigate();
  const { flightNumber } = useParams();

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="sm">
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            background: 'linear-gradient(180deg, #f0f4ff 0%, #d0e2ff 100%)',
            padding: 4,
            borderRadius: 2,
            boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.1)',
          }}
        >
          <Typography component="h1" variant="h4" color="primary" gutterBottom>
            Welcome to AirManage
          </Typography>
          <Typography variant="h6" color="textSecondary" gutterBottom>
            Your Flight Management Dashboard
          </Typography>
          <Box sx={{ mt: 4, width: '100%' }}>
            <Button
              startIcon={<TableViewIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': { bgcolor: 'primary.dark' },
                transition: 'all 0.3s ease',
              }}
              onClick={() => navigate(`/tabular-view/${flightNumber}`)}
            >
              Tabular View
            </Button>
            <Button
              startIcon={<FlightIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'secondary.main',
                color: 'white',
                '&:hover': { bgcolor: 'secondary.dark' },
                transition: 'all 0.3s ease',
              }}
              onClick={() => navigate(`/plane-view/${flightNumber}`)}
            >
              Plane View
            </Button>
            <Button
              startIcon={<PublicIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'info.main',
                color: 'white',
                '&:hover': { bgcolor: 'info.dark' },
                transition: 'all 0.3s ease',
              }}
              onClick={() => navigate(`/extended-view/${flightNumber}`)}
            >
              Extended View
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default Homepage;
