import React from 'react';
import { Button, Box, Typography, Container, CssBaseline } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import CreateIcon from '@mui/icons-material/Create';
import SaveAltIcon from '@mui/icons-material/SaveAlt';
import ViewListIcon from '@mui/icons-material/ViewList';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#6C757D',
    },
    info: {
      main: '#17A2B8',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

const RosterManagement = () => {
  const navigate = useNavigate();

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container component="main" maxWidth="md" sx={{ height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            background: 'linear-gradient(180deg, #f0f4ff 0%, #d0e2ff 100%)',
            padding: 6,
            borderRadius: 2,
            boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.1)',
            textAlign: 'center',
            width: '100%',
          }}
        >
          <Typography component="h1" variant="h4" color="textSecondary" gutterBottom>
            Roster Management
          </Typography>
          <Box sx={{ mt: 4, width: '100%' }}>
            <Button
              startIcon={<CreateIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': { bgcolor: 'primary.dark' },
                transition: 'all 0.3s ease',
                padding: 2,
                fontSize: '1.1rem',
              }}
              onClick={() => navigate(`/create-roster/sql`)}
            >
              Create Roster (SQL)
            </Button>
            <Button
              startIcon={<CreateIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': { bgcolor: 'primary.dark' },
                transition: 'all 0.3s ease',
                padding: 2,
                fontSize: '1.1rem',
              }}
              onClick={() => navigate(`/create-roster/nosql`)}
            >
              Create Roster (NoSQL)
            </Button>
            <Button
              startIcon={<SaveAltIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': { bgcolor: 'primary.dark' },
                transition: 'all 0.3s ease',
                padding: 2,
                fontSize: '1.1rem',
              }}
              onClick={() => navigate(`/export-roster/json`)}
            >
              Export Roster (JSON)
            </Button>
            <Button
              startIcon={<ViewListIcon />}
              variant="contained"
              sx={{ 
                width: '100%', 
                mb: 2, 
                bgcolor: 'primary.main',
                color: 'white',
                '&:hover': { bgcolor: 'primary.dark' },
                transition: 'all 0.3s ease',
                padding: 2,
                fontSize: '1.1rem',
              }}
              onClick={() => navigate(`/view-roster`)}
            >
              View Roster
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
};

export default RosterManagement;
